    $(document).ready(function(){
        var path = window.location.pathname;
        var page = path.split("/").pop();
        console.log(page)
        var els = document.querySelectorAll("a[href$='" + page + "']");
        if (els.length != 0) {
            if (els[0].dataset.length != 0) {
                if (els[0].dataset.current == els[0].id) {
                    els[0].parentNode.style.background = "#77c9d4"
                } else {
                    els[0].style.background = "#0b6ba1"
                    document.getElementById(els[0].dataset.current).style.background = "#77c9d4"
                }
            } else {
                els[0].parentNode.style.background = "#77c9d4"
            }
        }
    });
